import Vue from 'vue';
import Utils from '../util/util';
/*
 * 全局的过滤函数
 * */

function checkAddZone(num) {
  return num < 10 ? "0".concat(num.toString()) : num;
}

var globalFilter = {
  filterAddress: function filterAddress(value) {
    // 过滤地址
    if (!value) return value;
    var obj = value;
    return "".concat(obj.provinceName).concat(obj.cityName).concat(obj.countyName, " ").concat(obj.detailInfo);
  },
  filterTime: function filterTime(value) {
    // 过滤时间戳，返回值yyyy-mm-dd
    if (!value) {
      return value;
    }

    var date = new Date(value * 1000);
    var y = 1900 + date.getYear();
    var m = "0".concat(date.getMonth() + 1);
    var d = "0".concat(date.getDate());
    var val = "".concat(y, "-").concat(m.substring(m.length - 2, m.length), "-").concat(d.substring(d.length - 2, d.length));
    return val;
  },
  filterTimeYmdHms: function filterTimeYmdHms(value) {
    // 过滤时间戳，返回值yyyy-mm-dd ss
    if (!value) {
      return value;
    }

    var date = new Date(value * 1000);
    var y = 1900 + date.getYear();
    var m = "0".concat(date.getMonth() + 1);
    var d = "0".concat(date.getDate());
    var hh = date.getHours();
    var mm = "".concat(date.getMinutes());
    var ss = date.getSeconds() < 10 ? "0".concat(date.getSeconds()) : date.getSeconds();
    var val = "".concat(y, "-").concat(m.substring(m.length - 2, m.length), "-").concat(d.substring(d.length - 2, d.length), "  ").concat(hh, ":").concat(mm, ":").concat(ss);
    return val;
  },
  filterTimeYear: function filterTimeYear(value) {
    // 过滤时间戳, 返回值 今年:mm-dd 往年:yyyy-mm-dd
    var jy = 1900 + new Date().getYear();
    var date = new Date(value * 1000);
    var y = 1900 + date.getYear();
    var m = "0".concat(date.getMonth() + 1);
    var d = "0".concat(date.getDate());
    var val = "".concat(y, "-").concat(m.substring(m.length - 2, m.length), "-").concat(d.substring(d.length - 2, d.length));
    var thisYear = "".concat(m.substring(m.length - 2, m.length), "-").concat(d.substring(d.length - 2, d.length));

    if (jy === y) {
      return thisYear;
    }

    return val;
  },
  dateFormatter: function dateFormatter(nows) {
    if (!nows) return '';
    var now = new Date(nows);
    var year = now.getFullYear();
    var month = now.getMonth() + 1;
    month = checkAddZone(month);
    var date = now.getDate();
    date = checkAddZone(date);
    return "".concat(year, "-").concat(month, "-").concat(date);
  },
  dateTimeFormatter: function dateTimeFormatter(t) {
    if (!t) return '';
    t = new Date(t).getTime(); // eslint-disable-line

    t = new Date(t); // eslint-disable-line

    var year = t.getFullYear();
    var month = t.getMonth() + 1;
    month = checkAddZone(month);
    var date = t.getDate();
    date = checkAddZone(date);
    var hour = t.getHours();
    hour = checkAddZone(hour);
    var min = t.getMinutes();
    min = checkAddZone(min);
    var se = t.getSeconds();
    se = checkAddZone(se);
    return "".concat(year, "-").concat(month, "-").concat(date, " ").concat(hour, ":").concat(min, ":").concat(se);
  },
  filterTitle: function filterTitle(value) {
    var len = arguments.length > 1 && arguments[1] !== undefined ? arguments[1] : 9;
    return Utils.cutString(value, len);
  }
}; // 全局过滤器

Object.keys(globalFilter).forEach(function (k) {
  return Vue.filter(k, globalFilter[k]);
});
export default globalFilter;
