import { post, get, _delete } from '@/lin/plugin/axios'

class DataBase {
  async searchDBList(type) {
    const res = await get(`/sql/database/list/${type}`)
    return res
  }

  async getDbList() {
    const res = await get('/sql/database/list')
    return res
  }

  async createDB(data) {
    const res = await post('/sql/database/create', data)
    return res
  }

  async updateDB(data) {
    const res = await post('/sql/database/update', data)
    return res
  }

  async deleteDB(id) {
    const res = await _delete(`/sql/database/${id}`)
    return res
  }
}

export default new DataBase()
