import Utils from 'lin/util/util';
export default {
  props: {
    placeholder: {
      type: String,
      default: '请输入内容'
    }
  },
  data: function data() {
    return {
      keyword: ''
    };
  },
  created: function created() {
    var _this = this;

    // 节流搜索
    this.$watch('keyword', Utils.debounce(function (newQuery) {
      _this.$emit('query', newQuery);
    }, 1000));
  },
  methods: {
    clear: function clear() {
      this.keyword = '';
    },
    handleChange: function handleChange(change) {
      this.$emit('change', change);
    }
  }
};
