import Review from '@/model/track/review'
import Project from '@/model/track/project'
import Manager from '@/model/statistical/manager'

export default {
  name: 'review_edit',
  props: {
    operationType: {
      type: Number
    },
    reviewInfo: {
      type: Object
    }
  },
  data() {
    return {
      review_info: {},
      default_review_info: {},
      project_list: [],
      user_list: [],
    }
  },
  created() {
    this.getReviewInfo()
    this.getProjectList()
    this.getUserList()
  },
  methods: {
    getReviewInfo() {
      this.review_info = this.operationType === 1 ? { ...this.default_review_info } : { ...this.reviewInfo }
    },
    async saveReview() {
      const res = this.operationType === 2 ? await Review.updateReview(this.review_info) : await Review.createReview(this.review_info)
      if (res.code < window.MAX_SUCCESS_CODE) {
        this.$message.success(`${res.message}`)
        this.$emit('drawer-close')
      } else {
        this.$message.error('服务端异常')
      }
    },
    async getProjectList() {
      const res = await Project.getAllProkect()
      this.project_list = res
    },
    async getUserList() {
      const res = await Manager.getTesterList()
      this.user_list = res
    }
  }
}
