    handleOrderType(value) {
      //  产品类型
      if (value === 2) {
        this.transport_type_list = this.ltl_transport_type
        this.form.transport_label = '精准卡航'
        this.form.transport_type = 'JZKH'
      } else if (value === 1) {
        this.transport_type_list = this.exp_transport_type
        this.form.transport_label = '标准快递'
        this.form.transport_type = 'PACKAGE'
      }
    },
    handleDeliverInfo(value) {
      [this.form.delivery_province_code, this.form.delivery_city_code, this.form.delivery_area_code] = value
      this.form.delivery_province = this.getAddressName(value[0])
      this.form.delivery_city = this.getAddressName(value[1])
      this.form.delivery_area = this.getAddressName(value[2])
    },
    handleReceiverInfo(value) {
      [this.form.receiver_province_code, this.form.receiver_city_code, this.form.receiver_area_code] = value
      this.form.receiver_province = this.getAddressName(value[0])
      this.form.receiver_city = this.getAddressName(value[1])
      this.form.receiver_area = this.getAddressName(value[2])
    },
    getAddressName(key) {
      return this.address_dic[key]
    },
    async getOrderId() {
      this.order_list = ''
      this.order_list_dialog_visible = true
      this.loading = true
      this.order_list = await Orders.getGlobalOrder(this.form)
      this.loading = false

