result:
::-webkit-scrollbar {
  width: 0px;
  height: 0px;
}

.app-sidebar {
  /*menu的背景色*/
  background: #003C8D;

  &::-webkit-scrollbar {
    width: 0px;
    height: 0px;
  }

  .subMenuContent {
    overflow: hidden;
    text-overflow: ellipsis;
    white-space: nowrap;
  }

  .logo {
    width: $sidebar-width;
    height: $header-height;
    display: flex;
    justify-content: center;
    align-items: center;
    font-size: 30px;
    color: #fff;
    transition: all 0.5s ease-in-out;
    background-color: #122150;
    transition: all 0.3s linear;
    position: sticky;
    top: 0;
    left: 0;
    z-index: 99;
    img {
      width: 120px;
      transition: all 0.3s linear;
    }
  }

  .mobile-logo {
    width: 64px;
    height: 86px;
    display: flex;
    justify-content: center;
    align-items: center;
    background-color: #122150;
    transition: all 0.3s linear;

    img {
      width: 40px;
      height: 40px;
      transition: all 0.3s linear;
    }
  }

  .imgIcon {
    width: 16px;
    height: 16px;
    margin-right: 10px;
    margin-left: 5px;
    display: inline-block;
    transform: translateY(21px);
  }

  .iconfont {
    margin-right: 10px;
    margin-left: 5px;
    color: $submenu-title;
    height: $menuItem-height;
  }

  .search-display {
    position: relative;
    width: 80%;
    margin: 0 auto;
    height: 36px;
    border-bottom: 1px rgb(185, 190, 195) solid;
    cursor: pointer;

    .el-icon-search {
      position: absolute;
      left: 1px;
      top: 10px;
      color: rgb(185, 190, 195);
    }
  }

  .search {
    // margin-top: 20px;
    width: 80%;
  }
}
