export default {
  name: 'SelectMenu',
  props: {
    data: {
      type: Array
    },
    currentData: {
      type: Object
    },
    title: {
      type: String
    }
  },
  data() {
    return {
      value: ''
    }
  },
  watch: {
    currentData(data) {
      if (data !== undefined && data !== null) {
        this.value = data.name
      }
    }
  },
  methods: {
    changeData(index) {
      this.$emit('dataChange', this.data[index])
    }
  }
}
