import Function from '@/model/statistical/function'

export default {
  data() {
    return {
      date: '',
      search: {
        start_date: '',
        end_date: '',
      },
      chartData: {
        columns: ['name', 'dev_efficiency'],
        rows: []
      },
      chartSettings: {
        labelMap: {
          name: '产品线',
          dev_efficiency: '研发时效'
        },
      },
      chartExtend: {
        series: {
          barWidth: 20,
        },
        // x轴字体斜显示
        xAxis: {
          axisLabel: {
            interval: 0,
            formatter(value) {
              return value.split('').join('\n')
            }
          },
        }
      },
    }
  },
  mounted() {
    this.devEfficiency()
  },
  methods: {
    async chooseDate(date) {
      if (date === null) {
        this.search.start_date = ''
        this.search.end_date = ''
      } else {
        [this.search.start_date, this.search.end_date] = date
      }
      this.devEfficiency()
    },
    // 研发时效
    async devEfficiency() {
      const res = await Function.doEfficiency(this.search)
      if (res.success) {
        this.chartData.rows = res.result
      }
    },
  }
}
