import Manager from '@/model/statistical/manager'
import Version from '@/model/statistical/version'

export default {
  data() {
    this.chartSettings = {
      yAxisType: ['percent'],
      labelMap: {
        percent: '上线成功率',
      },
    }
    this.chartExtend = {
      series: {
        label: {
          normal: {
            show: true
          }
        }
      }
    }
    return {
      date: '',
      total: 0,
      search: {
        count: 10,
        page: 1,
        start_time: '',
        end_time: '',
      },
      pickerOptions: {
        shortcuts: [{
          text: '最近一周',
          onClick(picker) {
            const end = new Date()
            const start = new Date()
            start.setTime(start.getTime() - 3600 * 1000 * 24 * 7)
            picker.$emit('pick', [start, end])
          }
        }, {
          text: '最近一个月',
          onClick(picker) {
            const end = new Date()
            const start = new Date()
            //  dateCountForMonth是用以获取上月天数的变量
            const dateCountForMonth = new Date()
            dateCountForMonth.setMonth(dateCountForMonth.getMonth())
            dateCountForMonth.setDate(0)
            start.setTime(start.getTime() - 3600 * 1000 * 24 * dateCountForMonth.getDate())
            picker.$emit('pick', [start, end])
          }
        }, {
          text: '最近90天',
          onClick(picker) {
            const end = new Date()
            const start = new Date()
            start.setTime(start.getTime() - 3600 * 1000 * 24 * 90)
            picker.$emit('pick', [start, end])
          }
        }]
      },
      product_line_list: [],
      system: '',
      system_list: [],
      tableData: [],
      rollback_rate_visible: false,
      version_count_visible: false,
      chartData: {
        columns: [],
        rows: []
      },
      chartSettings: {},
      chartExtend: {
        // 柱子宽度
        series: {
          barWidth: 20,
          // label: { show: true, position: "top", color: "gray"}
        },
        // x轴字体斜显示
        xAxis: {
          axisLabel: {
            interval: 0,
            formatter(value) {
              return value.split('').join('\n')
            }
          },
        }
      },
      isRollbackList: [{
        label: '否',
        value: false,
      }, {
        label: '是',
        value: true,
      }],
      edit_type: 0, // 1新增 2修改
      edit_dialog_visible: false,
      default_version_info: {
        createDate: '',
        is_rollback: false,
        is_emergency: false,
        system: '',
        versionType: null,
        description: ''
      },
      version_info: {}
    }
  },
  mounted() {
    this.getProductLineList()
    this.getSystemList()
    this.searchVersionList()
  },
  methods: {
    // 获取产品线列表
    async getProductLineList() {
      this.product_line_list = await Manager.getProductList()
    },
    // 获取系统列表
    async getSystemList() {
      const res = await Version.getSysList()
      this.system_list = res
    },
    // 选择时间
    async chooseDate(date) {
      this.search.page = 1
      if (date === null) {
        this.search.start_date = ''
        this.search.end_date = ''
      } else {
        [this.search.start_date, this.search.end_date] = date
      }
      this.searchVersionList()
    },
    async searchVersionList() {
      const res = await Version.searchVersionList(this.search)
      this.tableData = res.items
      this.total = res.total
      this.tableData.forEach(c => {
        c.irb = c.is_rollback ? '是' : '否'
        c.iemg = c.is_emergency ? '是' : '否'
      })
    },
    async statisticalRollbackRate() {
      this.chartSettings = {
        yAxisType: ['percent'],
        labelMap: {
          percent: '上线成功率',
        }
      }
      this.chartData.columns = ['create_date', 'percent']
      const res = await Version.queryVersionPercent(this.search)
      if (res.success) {
        this.rollback_rate_visible = true
        this.chartData.rows = res.result
      } else {
        this.$message.error(res.msg)
      }
    },
    async statisticalVersionCount() {
      this.chartSettings = {
        labelMap: {
          daily_count: '日常版本',
          project_count: '项目版本'
        },
        stack: { name: ['daily_count', 'project_count'] }
      }
      this.chartData.columns = ['department_name', 'daily_count', 'project_count']
      const res = await Version.queryVersionCount(this.search)
      if (res.success) {
        this.version_count_visible = true
        this.chartData.rows = res.result
      } else {
        this.$message.error(res.msg)
      }
    },
    exportExcel() {
      this.$axios({
        method: 'post',
        url: '/statistical/version/download',
        responseType: 'blob',
        data: this.search
      }).then(
        res => {
          const link = document.createElement('a')
          link.style.display = 'none'
          link.href = URL.createObjectURL(new Blob([res]))
          link.setAttribute('download', '版本信息.xlsx')
          document.body.appendChild(link)
          link.click()
          URL.revokeObjectURL(link.href)
          document.body.removeChild(link)
        }
      )
    },
    insertVersion() {
      this.edit_type = 1
      this.edit_dialog_visible = true
      this.version_info = { ...this.default_version_info }
    },
    async saveVersion() {
      const res = this.edit_type === 1 ? await Version.createVersion(this.version_info) : await Version.updateVersion(this.version_info)
      if (res.code < window.MAX_SUCCESS_CODE) {
        this.$message.success(`${res.message}`)
        this.edit_dialog_visible = false
        this.searchVersionList()
      } else {
        this.$message.error('服务端异常')
      }
    },
    handleEdit(row) {
      // 将操作状态设置为2：编辑
      this.edit_type = 2
      this.edit_dialog_visible = true
      // 获取case信息
      this.version_info = { ...row }
    },
    async deleteVersion(id) {
      const res = await Version.deleteVersion(id)
      if (res.code < window.MAX_SUCCESS_CODE) {
        this.$message.success(`${res.message}`)
        this.searchVersionList()
      } else {
        this.$message.error('删除版本功能异常')
      }
    },
    handleSizeChange(val) {
      this.search.count = val
      this.searchVersionList()
    },
    handleCurrentChange(val) {
      this.search.page = val
      this.searchVersionList()
    },
    isCore(system) {
      const coreList = ['CRM', 'OWS', 'PDA-SP', 'BRMS', 'CCS', 'DPAPP', 'MCRM', 'MOW', 'NDOP', 'OCR', 'OSS', 'OMS',
        'NVAS', 'WQS', 'OCB', 'PPC', 'RCP', 'UAP', 'BDM', 'WMC', 'RNA', 'PDC', 'WKPDAM', 'OWS', 'FOSS-P', 'FOSS-PDA',
        'FOSS-S', 'ECS-P', 'ECS-PDA', 'ECS-S', 'ECS-ZHCX', 'ECS-ZSJ', 'GAS', 'ACS']
      const result = {}
      if (coreList.includes(system)) {
        result.bool = true
        result.value = '核'
      } else {
        result.bool = false
        result.value = '非'
      }
      return result
    }
  }
}
