import HttpCase from '@/model/autotest/http-case'

export default {
  name: 'http-case-chose',
  props: {
    value: {
      type: Object
    }
  },
  data() {
    return {
      search: {
        page: 1,
        count: 10,
        case_name: '',
        category: '',
        creator_code: '',
      },
      total: 0,
      category_list: [],
      creator_list: [],
      case_list: [],
    }
  },
  created() {
    this.searchCaseList()
    this.getCreatorList()
  },
  methods: {
    async searchCaseList() {
      const res = await HttpCase.searchCaseList(this.search)
      this.case_list = res.items
      this.total = res.total
    },
    // 获取创建人列表
    async getCreatorList() {
      const res = await HttpCase.getCreatorList()
      this.creator_list = res
    },
    handleSizeChange(val) {
      this.search.count = val
      this.searchCaseList()
    },
    handleCurrentChange(val) {
      this.search.page = val
      this.searchCaseList()
    },
    rowClick(row) {
      this.$emit('input', row)
      this.$emit('dialogClose')
    }
  }
}
